import React, {Component} from 'react';
import { Form,Col } from 'reactstrap';
import Button from '@material-ui/core/Button'; 
class Grade extends Component {

   state ={
      salaryStructure:[],
      product: {
         row_id:0,
         from_salary:0,
         to_salary:0,
         tax_in_percent:0,
         sur_plus_in_percent:"",
         start_year:"",
         end_year:"",
      }
   }
   componentDidMount() {
      this.getLogin();
    }

    getLogin = _ => {
      fetch('http://localhost:4002/salaryStructure')         
      .then(response => response.json())
      .then(response => this.setState({ Login: response.data }))
      .catch(err => console.error(err))
    }
    addProduct = _ => {
      const {product} = this.state;
      fetch(`http://localhost:4002/salaryStructure/add?login_id=${product.login_id}&password=${product.password}`)
      .then(response => response.json())
      .then(this.getLogin)
      .catch(err => console.error(err)) 
  }

    renderProduct = ({ login_id, password }) => <div key={login_id }>{password}</div>

    render()
    {
      const { Login, product } = this.state;
    return (
        <div className="container">
             {Login.map(this.renderProduct)}
        <h1 className="row justify-content-center" >Grade Details</h1> 
        <Form>



<div class="row">
       <div class="col">

      <input type="text"
          value={product.login_id}
          onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
      class="form-control" placeholder="Grade Name" /> 
   </div>
<div class="col">

<input type="text" 
    value={product.login_id}
    onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
class="form-control" placeholder="Basic Salary" />
</div>
   </div> <br/>

   <div class="row">
       <div class="col">

      <input type="text" 
          value={product.login_id}
          onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
      class="form-control" placeholder="HRA" /> 
   </div>
<div class="col">

<input type="text" 
    value={product.login_id}
    onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
class="form-control" placeholder="Medical Allowance" />
</div>
   </div> <br/>



   <div class="row">
       <div class="col">

      <input 
          value={product.login_id}
          onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
      type="text" class="form-control" placeholder="Bonous" /> 
   </div>
<div class="col">

<input type="text" 
    value={product.login_id}
    onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
class="form-control" placeholder="Provident Fund" />
</div>
   </div>  <br/>


   <div class="row">
       <div class="col">

      <input type="text" 
          value={product.login_id}
          onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
      class="form-control" placeholder="Professional Tax" /> 
   </div>
<div class="col">

<input type="text" 
    value={product.login_id}
    onChange={e => this.setState({product: {...product,login_id: e.target.value }})}
class="form-control" placeholder="Current CTC" />
</div>
   </div>  <br/>

   <div>
   <Col md={{size:10 ,offset:5}}>   <Button 
    onClick = {this.addProduct} 
   variant="contained"  >
Submit
</Button>&nbsp; &nbsp;&nbsp;
<Button variant="contained" color="primary" >
Clear
</Button></Col>
</div>

</Form>
        </div>
    );
  }
  }
  export default Grade;
