import React, { Component } from "react";
    import Form from 'react-bootstrap/Form'
    import Button from 'react-bootstrap/Button'

    export default class Login extends Component {

      state = {
          Login:[],
          Adding:{
            login_id:"",
            password:""
      }
    }  

    componentDidMount() {
      this.getLogin()
    }

    getLogin = _ => {
      fetch('http://localhost:4002/Login')         
      .then(response => response.json())
       .then(response => this.setState({ Login:response.data }))
       .catch(err => console.error(err))
    }

addAdding = _ => {
      const {Adding} = this.state;
      fetch(`http://localhost:4002/Login/add?login_id=${Adding.login_id}&password=${Adding.password})`)
      .then(response => response.json())
      .then(this.getLogin)
      .catch(err => console.error(err)) 
    }

    renderAdding = ({login_id,password }) => <div key={login_id }>{password}</div>

      // validateForm() {
      //   return this.state.email.length > 0 && this.state.password.length > 0;
      // }

      handleChange = event => {
        this.setState({
          [event.target.id]: event.target.value
        });
      }

      handleSubmit = event => {
        event.preventDefault();
      }

      render() {
        const { login_id, Adding } = this.state;
        return (
          <div className="Login">
            {/* { Login.map(this.renderAdding)}   */}
            <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="email" bsSize="large">
                <h3>E-Mail</h3>
                <Form.Control
                  autoFocus
                  type="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                />
              </Form.Group>
              <Form.Group controlId="password" bsSize="large">
              <h3>Password</h3>
                <Form.Control
                  value={this.state.password}
                  onChange={this.handleChange}
                  type="password"
                />
              </Form.Group>
              <Button
                block
                bsSize="large"
                // disabled={!this.validateForm()}
                type="submit"
              >
                Login
              </Button>
            </Form>
          </div>
        );
      }
    }
